import React from "react";

const ServiceDetailsBanner = () => {
  return (
    <div className="banner-area bg-sky pd-bottom-120 banner-small-inner  text-center">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-11">
            <h4
              className=""
              data-aos="fade-up"
              data-aos-duration="1500"
              data-aos-delay="300"
            >
              Insurance Tax
            </h4>
            <h3
              className=""
              data-aos="fade-up"
              data-aos-duration="1500"
              data-aos-delay="400"
            >
              We have almost 25+ years of experience in this tax advisor
              financial consulting field
            </h3>
            <div
              className="scroll-down top_image_bounce "
              data-aos="fade-up"
              data-aos-duration="1500"
              data-aos-delay="500"
            >
              <a href="#service-details-area">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  xmlnsXlink="http://www.w3.org/1999/xlink"
                  width="40"
                  height="80"
                  viewBox="0 0 40 80"
                >
                  <image
                    id="scroll"
                    width="40"
                    height="80"
                    xlinkHref="data:img/png;base64,"
                  />
                </svg>
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ServiceDetailsBanner;
